package net.cbtltd.shared.finance.gateway;

/**
 * Customer payment profile generated by the payment gateway. 
 */
public class CustomerPaymentProfile {
	private int id;
	private int reservationId;
	private String customerProfileId;
	private String customerPaymentProfileId;
	private int gatewayId;
	
	public CustomerPaymentProfile() {
		super();
	}
	
	public CustomerPaymentProfile(int reservationId, String customerProfileId, String customerPaymentProfileId, int gatewayId) {
		this.reservationId = reservationId;
		this.customerProfileId = customerProfileId;
		this.customerPaymentProfileId = customerPaymentProfileId;
		this.gatewayId = gatewayId;
	}
	
	public int getReservationId() {
		return reservationId;
	}
	
	public void setReservationId(int reservationId) {
		this.reservationId = reservationId;
	}
	
	public String getCustomerProfileId() {
		return customerProfileId;
	}
	
	public void setCustomerProfileId(String customerProfileId) {
		this.customerProfileId = customerProfileId;
	}
	
	public String getCustomerPaymentProfileId() {
		return customerPaymentProfileId;
	}
	
	public void setCustomerPaymentProfileId(String customerPaymentProfileId) {
		this.customerPaymentProfileId = customerPaymentProfileId;
	}

	public int getGatewayId() {
		return gatewayId;
	}

	public void setGatewayId(int gatewayId) {
		this.gatewayId = gatewayId;
	}

	public int getId() {
		return id;
	}

	public void setId(int id) {
		this.id = id;
	}
}
